                insert into insecte(nom,image_url,description_insecte,partie1,partie2,famille,diagnostic)
                values('Cicadulina bipunctata','','','Cicadulina','','Cicadellidae','The male genitalia are distinctive, with the form of the pygofer process diagnostic (Fig. 3.302). Female ovipositor with black tip (Fig. 3.306).(Wilson & Claridge 1991)');
            
                insert into insecte(nom,image_url,description_insecte,partie1,partie2,famille,diagnostic)
