                insert into insecte(nom,image_url,description_insecte,partie1,partie2,famille,diagnostic)
                values('Graphocephala dolobrata','http://insectvectors.science/files/specimens/E000736.jpg','General colour purplish brown to purplish black. Crown yellow to ivory with deep broad purplish black markings; pronotum with ivory and purplish black markings; scutellum with distinct yellow to ivory longitudinal spot on middle, lateral angles purple; elytra purple to purplish black with ivory band along claval suture, cIavus sometimes with ivory lines; colour deeper in males.(Nielson, 1968)','Graphocephala','Medium size, slightly robust Species. Length of male 5.50—5.70 mm., female 6.00—6.40 mm. (Nielson, 1968)','Cicadellidae','This species, allied to confluens, is difficult to separate on the basis of the genitalia. The shaft of the aedeagus of dolobrata in lateral aspect is about the same width distally as basally and the caudal margin of the pygofer is truncate. (Nielson 1968)');
            
                insert into insecte(nom,image_url,description_insecte,partie1,partie2,famille,diagnostic)
                values('Recilia dorsalis','http://insectvectors.science/files/specimens/i14951.jpg','General colour light grey. Crown and pronotum light grey with light infuscations of brown; elytra grey with conspicuous broad, brown, zigzag longitudinal band.(Nielson, 1968)','Recilia','Small, linear species. Length of male 3.20—3.40 mm., female 3.70—3.80 mm. (Nielson, 1968)','Cicadellidae','R. dorsalis is among the easiest of the rice-associated leafhoppers to identify by virtue of the distinctive markings of the fore- wings in both males and females, hence the name `zig-zag leafhopper`.(Wilson & Claridge 1991)');
            
                insert into insecte(nom,image_url,description_insecte,partie1,partie2,famille,diagnostic)
