                insert into insecte(nom,image_url,description_insecte,partie1,partie2,famille,diagnostic)
                values('Gyponana hasta','http://insectvectors.science/files/specimens/i14223.jpg','General color light tan. Crown, pronotum, and elytra light tan, immaculate.(Nielson, 1968)','Gyponana','Large, robust species. Length of male 8.60—9.20 mm., female 8.50—10.40 mm. (Nielson, 1968)','Cicadellidae','This species is related to lamina and can be separated by the aedeagal shaft, which is nearly straight in lateral aspect, and the style with apical half strongly curved laterally. (Nielson 1968)');
            
