                insert into insecte(nom,image_url,description_insecte,partie1,partie2,famille,diagnostic)
                values('Toya propinqua','http://insectvectors.science/files/specimens/i14806.jpg','','Toya','Male/female 2.8-3.7 mm. (Nielson, 1968)','Delphacidae','Externally Toya species may be separated from other common delphacids by the short vertex and coloration; the carinae are pale yellow and the area between them is dark brown (Fig. 3.162). T. propinqua is most easily separated by the Y-shaped process on the diaphragm of the male genitalia (Fig. 3.167).(Wilson & Claridge 1991)');
            
