                insert into insecte(nom,image_url,description_insecte,partie1,partie2,famille,diagnostic)
                values('Cuerna costalis','http://insectvectors.science/files/specimens/E000914.jpg','General color, reddish black to black with prominent yellow or ivory band running laterally from anterior margin of head to eighth tergum; head, pronotum, and scutellum black with numerous coarse yellow markings.(Nielson, 1968)','Cuerna','Large, robust species. Length of male 7.70—9.10 mm., female 8.20—9.60 mm. (Nielson, 1968)','Cicadellidae','From yuccae, to which it is similar in male genital characteristics, costalis can be distinguished by the female eighth sternum, which has a single broad, bell-shaped plate, and by its geographical distribution.');
            
                insert into insecte(nom,image_url,description_insecte,partie1,partie2,famille,diagnostic)
