                insert into insecte(nom,image_url,description_insecte,partie1,partie2,famille,diagnostic)
                values('Dalbulus elimatus','http://insectvectors.science/files/specimens/i14259.jpg','General colour yellowish brown or tan. Crown with two distinct small subquadrate spots; pronotum yellow with four longitudinal light brown bands; elytra tan, subhyaline.(Nielson, 1968)','Dalbulus','Small, linear species. male 3.60—3.90., female 3.80—4.00 mm. (Nielson, 1968)','Cicadellidae','This species is similar to Dalbulus maidis in general habitus and can easily be separated by the male pygofer with the ventral margin distinctly sclerotized, the distinctive aedeagus, and the female seventh sternum, which is elongate and acutely angled distally.(Nielson 1968)');
            
                insert into insecte(nom,image_url,description_insecte,partie1,partie2,famille,diagnostic)
