                insert into insecte(nom,image_url,description_insecte,partie1,partie2,famille,diagnostic)
                values('Xyphon flaviceps','http://insectvectors.science/files/specimens/E000788.jpg','General color yellowish red with pronounced venation. Crown light reddish; pronotum yellowish red; elytra yellowish brown, reticulated at apex, veins dull yellow.(Nielson, 1968)','Xyphon','Medium size, slender species. Length of male 4.30—4.90 mm, female 5.00—6.00 mm. (Nielson, 1968)','Cicadellidae','From fulgida, to which it is similar in genital characteristics, flaviceps can be separated by the aedeagus with the shaft broad basally and narrowed distally.(Nielson 1968)');
            
                insert into insecte(nom,image_url,description_insecte,partie1,partie2,famille,diagnostic)
